package com.java.stream;

public class Item {
	
	private Long codigo;
	private Integer qtd;
	private Double preco;
	private String nome;
	
	public Item() {
		// TODO Auto-generated constructor stub
	}

	public Item(Long codigo, Integer qtd, Double preco, String nome) {
		super();
		this.codigo = codigo;
		this.qtd = qtd;
		this.preco = preco;
		this.nome = nome;
	}

	public Long getCodigo() {
		return codigo;
	}

	public void setCodigo(Long codigo) {
		this.codigo = codigo;
	}

	public Integer getQtd() {
		return qtd;
	}

	public void setQtd(Integer qtd) {
		this.qtd = qtd;
	}

	public Double getPreco() {
		return preco;
	}

	public void setPreco(Double preco) {
		this.preco = preco;
	}
	
	public String getNome() {
		return this.nome;
	}
	
	public void setNome(String nome) {
		this.nome = nome;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((codigo == null) ? 0 : codigo.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Item other = (Item) obj;
		if (codigo == null) {
			if (other.codigo != null)
				return false;
		} else if (!codigo.equals(other.codigo))
			return false;
		return true;
	}

	@Override
	public String toString() {
		return "Codigo:" + this.codigo + ", Qtd:" + this.qtd + ", Preco:" + this.preco+ " Nome:" + this.nome ;
	}
	
	

}
